import { WorkboxPlugin, CacheKeyWillBeUsedCallbackParam, RequestWillFetchCallbackParam } from 'workbox-core/types';
/**
 * Workbox only allows GET requests to be cached out-of-the-box, so this plugin
 * allows for changing the method of a request so that it can be cached.
 */
export default class ChangeMethodPlugin implements WorkboxPlugin {
    urls: RegExp[];
    constructor(urls: RegExp[]);
    urlMatches(url: string): boolean;
    /**
     * Workbox requires cache keys to be GET method, so this forces the method when it is determined
     * that the cache key will be used.
     * If the request has a body, the body will be added to the URL for the cache key
     * using the POST_BODY_QUERY_PARAM.
     */
    cacheKeyWillBeUsed({ request }: CacheKeyWillBeUsedCallbackParam): Promise<string>;
    /**
     *
     */
    requestWillFetch({ request }: RequestWillFetchCallbackParam): Promise<Request>;
}
